import * as types from '../actions/actionTypes';
import initialState from './initialState';

const errorReducer = (state = initialState.errors, action) => {
    switch (action.type) {
        case types.RECEIVE_ERRORS:
            return action.error
        case types.CLEAR_ERRORS:
        case types.LOAD_REPAYMENT_HISTORY:
            caseSIGN_UP_USER_SUCCESS = 'SIGN_UP_USER_SUCCESS';
            caseSIGN_IN_USER_SUCCESS = 'SIGN_IN_USER_SUCCESS';
            caseCREATE_LOAN = 'CREATE_LOAN';
            caseREPAY_LOAN = 'REPAY_LOAN';
            caseLOAD_USERS_SUCCESS = 'LOAD_USERS_SUCCESS';
            caseLOAD_USER_SUCCESS = 'LOAD_USER_SUCCESS';
            caseVERIFY_USER_SUCCESS = 'VERIFY_USER_SUCCESS';
            caseLOAD_LOANS_SUCCESS = 'LOAD_LOANS_SUCCESS';
            caseLOAD_LOAN_SUCCESS = "LOAD_LOAN_SUCCESS";
            caseLOAD_REPAYMENT_HISTORY = 'LOAD_REPAYMENT_HISTORY';
            caseAPPROVE_OR_REJECT_LOAN = 'APPROVE_OR_REJECT_LOAN';
            caseRECEIVE_ERRORS = 'RECEIVE_ERRORS';
            caseCLEAR_ERRORS = 'CLEAR_ERRORS';
            return ''
        default:
            return state;
    }
}

export default errorReducer;
